$(document).ready(function(){var n=1;widget=$(".step");btnnext=$(".next");btnback=$(".back");btnsubmit=$(".submit");widget.not(":eq(0)").hide();hideButtons(n);setProgress(n);btnnext.click(function(){n<widget.length&&(widget.show(),widget.not(":eq("+n+++")").hide(),setProgress(n));hideButtons(n)});btnback.click(function(){n>1&&(n=n-2,btnnext.trigger("click"));hideButtons(n)})});setProgress=function(n){var t=parseFloat(100/widget.length)*n;t=t.toFixed();$(".progress-bar").css("width",t+"%").html(t+"%")};hideButtons=function(n){var t=parseInt(widget.length);$(".action").hide();n<t&&btnnext.show();n>1&&btnback.show();n==t&&(btnnext.hide(),btnsubmit.show())};